const SWITCH_FOLLOW = "SWITCH_FOLLOW";
const SET_USERS = "SET_USERS";
const SET_CURRENT_PAGE = "SET_CURRENT_PAGE";
const SET_TOTAL_PAGE_COUNT = "SET_TOTAL_PAGE_COUNT";

let initialState = {
    usersData: [],
    pageSize: 5,
    currentPage: 1,
    pageCount: 55
}

let users_reducer = (state = initialState, action) => {

    switch (action.type) {

        case SWITCH_FOLLOW: {

                return {
                    ...state,
                    usersData: state.usersData.map( u => {
                        if(u.id === action.userId){
                            return {...u, followed: !u.followed}
                        }
                        return u
                    })
                }

            return state

        }
        case SET_USERS: {
            return { ...state, usersData: [...action.users]}
        }
        case SET_CURRENT_PAGE: {
            return { ...state, currentPage: action.currentPage}
        }
        case SET_TOTAL_PAGE_COUNT: {
            return { ...state, pageCount: action.count}
        }
    }

    return state
}

export const switch_followAC = (userId) => ({type: SWITCH_FOLLOW, userId});
export const setUsersAC = (users) => ({type: SET_USERS, users});
export const setCurrentPageAC = (currentPage) => ({type: SET_CURRENT_PAGE, currentPage});
export const setPageCountAC = (count) => ({type: SET_TOTAL_PAGE_COUNT, count});

export default users_reducer
