import React from 'react';
import NewsItem from "./newsItem/NewsItem";
import s from './News.module.css'

const News = (props) => {
    return (
        <div className={s.postArea}>
            {props.state.news.map(post => (
                <NewsItem post = {post} />
            ))}
        </div>
    );
}

export default News;
