import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";

const Dialogs = (props) => {
    let newMessage = React.createRef();

    let onAddMessage = () => {
        props.addMessage();
    }

    let onUpdateMessage = (e) => {
        let text = newMessage.current.value;
        props.updateMessage(text);
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {props.state.userData.map((user) =>
                    <DialogItem props = {user}/>
                )}

            </div>
            <div className={s.messages}>
                {props.state.messageData.map((message) =>
                    <Message props = {message}/>
                )}
                <div className={s.inputArea}>
                    <textarea placeholder='Enter message...' className={s.input} onChange={ onUpdateMessage } value={ props.state.newMessageText } ref={newMessage}/>
                    <button className={s.btn} onClick={onAddMessage}>Send</button>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
