import React, { useState } from 'react'
import style from './Users.module.css'
import Paginator from '../common/paginator/Paginator';
import Preloader from '../common/Preloader/Preloader';
import UserItem from './UserItem'
import Filter from './Filter';
import { UserType } from '../../Types/Types';

type PropsType = {
    state: Array<UserType>
    unfollow: (userId: number) => void
    follow: (userId: number) => void
    currentPage: number
    pageCount: number
    pageSize: number
    pageEvent: (n: number) => void
    isFetching: boolean
    toggleProgressArr: Array<number>
}

const Users: React.FC<PropsType> = (props) => {

    const [isShowFolowing, setIsShowFollowing] = useState(false)

    const switch_follow = (followed: boolean, userId: number) => {
        if (followed) {
            props.unfollow(userId)
        } else {
            props.follow(userId)
        }
    }

    return <div>

        <Paginator 
            currentPage = {props.currentPage}
            pageCount = {props.pageCount}
            pageSize = {props.pageSize}
            pageEvent = {props.pageEvent}
            portionSize = {10}/>

        <Filter showFollowing = {() => setIsShowFollowing(true)} showAllUsers = {() => setIsShowFollowing(false)}/>

        { props.isFetching 
            ? <Preloader /> 
            : props.state.map(user =>
                isShowFolowing 
                    ? user.followed && <UserItem user = {user} switch_follow = {switch_follow} toggleProgressArr = {props.toggleProgressArr}/>  
                    : <UserItem user = {user} switch_follow = {switch_follow} toggleProgressArr = {props.toggleProgressArr}/>
        )}
    </div>
}

export default Users
