import React from 'react';
import Profile from "./Profile";
import {connect} from "react-redux";
import {check_auth} from "../../redux/profile_reducer";
import {Redirect, withRouter} from "react-router-dom";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId;
        if(!userId) userId = 2
        this.props.check_auth(userId)
    }

    render() {
        if (!this.props.isAuth){
            <Redirect to='/login'/>
        }
        return (
            <Profile state = {this.props.state}/>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        state: state.profilePage,
        isAuth: state.auth.isAuth
    }
}

let UrlUserContainer = withRouter(ProfileContainer)

export default connect( mapStateToProps, { check_auth })(UrlUserContainer);
