import React from 'react';
import s from './Post.module.css';
import sNews from '../../../News/newsItem/NewsItem.module.css'
import commentPhoto from '../../../../img/comment2.svg'
import likesPhoto from '../../../../img/heart.svg'
import commentUserPhoto from '../../../../img/user22.jpg'
import likesPhotoActive from '../../../../img/heart_active.svg'
import userPhoto from '../../../../img/user.svg'
import {Element} from '../../../common/FormControl/FormControl';
import { reduxForm, Field } from 'redux-form';

const Post = (props) => {

  let onAddComment = (data) => {
      props.addComment(props.post.id, data.comment);
      props.updateCommentCount(props.post.id)
      props.reset('commentForm')
  }

  let onAddLike = (id) => {
      props.updateLikeCount(id)
  }

  return (
      <div className={sNews.newsItem}>
          <span style = {{display: "flex", alignItems: "center"}}>
              <span>
                  {props.avatar 
                      ? <img src={props.post.avatar} alt="PHOTO" className={sNews.userImg}/>
                      : <img src={userPhoto} alt="PHOTO" className={sNews.userImg}/>
                  }
              </span>
              <span className={sNews.user}>{props.profile.fullName || ''}</span>
          </span>
          <span className={sNews.newsTitle}> {props.post.title} </span>
          <div>
            { props.post.photo && <img src={props.post.photo} alt="Photo"/> }
          </div>
          <div className={sNews.reviewCount}> 
              <span>
                  {
                  props.post.liked 
                  ? <img src={likesPhotoActive} alt="PHOTO" className={sNews.reviewIcon} onClick={() => onAddLike(props.post.id)}/>
                  : <img src={likesPhoto} alt="PHOTO" className={sNews.reviewIcon} onClick={() => onAddLike(props.post.id)}/>
              } {props.post.likesCount} 
              </span>
               <span> 
                  <img src={commentPhoto} alt="PHOTO" className={sNews.reviewIcon}/>{props.post.CommentsCount} 
               </span> 
          </div>
          <CommentFormRedux onSubmit={onAddComment}/>
          <div className={sNews.commentsArea}>
              { props.post.comments.map(item => (
                  <div className={sNews.commentsItem} key={item.userId}>
                      {
                      item.photo 
                          ? <img src={item.photo } alt="Photo" className={sNews.commentPhoto}/>
                          : <img src={ commentUserPhoto } alt="Photo" className={sNews.commentPhoto}/>
                      }
                      <div>
                          <span>{item.fullName}</span>
                          <p style={{paddingLeft: '15px', margin: 0}}>{item.text}</p>
                      </div>
                  </div>
              ))}
          </div>
      </div>
  );
}

const Input = Element('input');

const CommentForm = (props) => {
    return <form className={sNews.inputComment} onSubmit={props.handleSubmit}>
        <Field placeholder='Write your comment' name='comment' component = {Input}/>
        <button type = 'submit' className='btn'>Send</button>
    </form>
}

const CommentFormRedux = reduxForm({form: 'commentForm'})(CommentForm)

export default Post;
