import React from 'react';
import NewsItem from "./newsItem/NewsItem";
import s from './News.module.css'
import {addNewComment, updateNewComment} from "../../redux/news_reducer";
import {Redirect} from "react-router-dom";

const News = (props) => {
    return (
        <div className={s.newsWrap}>
            <div className={s.story}/>
            <div className={s.postArea}>
                {props.state.news.map(post => (
                    <NewsItem post={post} newCommentText = {props.state.newCommentText} addComment={props.addNewComment} updateComment={props.updateNewComment}/>
                ))}
            </div>
        </div>
    );
}

export default News;
