import { Field, Form, Formik } from 'formik'
import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import { AppStateType } from '../../redux/store-redux'
import photoUser from '../../img/user.svg'
import { UserType } from '../../Types/Types'

const HeaderSearchForm = () => {

    const friends = useSelector((state: AppStateType) => state.usersPage.friends)
    const [searchFriends, setSearchFriends] = useState([])

    const submit = (values: FormValuesType, {setSubmitting} : {setSubmitting: (isSubmitting: boolean) => void}) => {
        setSubmitting(false)
        let searchFriends = friends.filter((item) => {
            return item.name && item.name.toLowerCase().includes(values.fullName)
        })
        //@ts-ignore
        setSearchFriends(setSearchFriends)
    }

    return <div style = {{position: "relative"}}>
        <Formik
            initialValues =  {{
                fullName: "",
            }}
            onSubmit = {submit}>
            {
                ({values, handleChange, submitForm}) => (
                <Form>
                    <Field type = "text" name = "fullName" placeholder = "Enter name..." value = {values.fullName} onChange = {(e: any) => {handleChange(e); setTimeout(submitForm, 0)}}/>
                </Form>
                )
            }
        </Formik>
        <div style = {{display: "flex", flexDirection: "column"}}>
            {
                searchFriends && searchFriends.map((item) => {
                    return <div style = {{display: "flex", height: "30px"}}>
                        <img src={item.photos.large ? item.photos.large : photoUser} style = {{width: "25px"}} alt="PHOTOS"/>
                        <span style = {{marginLeft: "15px"}}>{item.name}</span>
                    </div>
                })
            }  
        </div>   
    </div>
}

export default HeaderSearchForm

type FormValuesType = {
    fullName: string
}


