const UPDATE_NEW_POST = "UPDATE-NEW-POST";
const ADD_NEW_POST = "ADD-NEW-POST";

let initialState = {
    postsData: [
        {"id": 1, "message": "Hello, how are you?", "likesCount": 5},
        {"id": 2, "message": "My, first post!", "likesCount": 2},
        {"id": 3, "message": "Hello, everyone", "likesCount": 10},
    ],
    newPostText: ''
}

let profile_reducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_NEW_POST:
            let newPost = {
                "id": 5,
                "message": state.newPostText,
                "likesCount": 14
            }

            if (state.newPostText.length > 0) {
                state.postsData.push(newPost);
                state.newPostText = '';
            }

            return state

        case UPDATE_NEW_POST:
            state.newPostText = action.newText;

            return state
    }

    return state
}

export const addPostActionCreater = () => ({type: ADD_NEW_POST});
export const updateNewPostActionCreater = (text) => ({type: UPDATE_NEW_POST, newText: text});

export default profile_reducer
