import React from 'react';
import s from './NewsItem.module.css'

const NewsItem = (props) => {

    let onUpdateComment = (e) => {
        let text = e.target.value;
        debugger
        props.updateComment(text);
    }

    let onAddComment = () => {
        props.addComment();
    }

    return (
        <div className={s.newsItem}>
            <span className={s.user}> User - {props.post.id} </span>
            <span className={s.newsTitle}> {props.post.title} </span>
            <div><img src={props.post.photo} alt="Photo"/></div>
            <div className={s.reviewCount}> <span> likes - {props.post.likesCount} </span>  <span> Comments - {props.post.CommentsCount} </span> </div>
            <div className={s.commentsArea}>
                <div className={s.inputComment}>
                    <input placeholder='Write your comment' value={props.newCommentText} onChange={ onUpdateComment }/>
                    <button className='btn' onClick={onAddComment}>Send</button>
                </div>
                {props.post.comments.map(item => (
                    <div className={s.commentsItem} key={item.userId}>
                        <span>User - {item.userId} </span>
                        <span>{item.text}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default NewsItem;
