import React, { useState } from 'react';
import { reduxForm, Field } from 'redux-form';
import { Element } from '../../common/FormControl/FormControl';

const Input = Element('input')
const Textarea1 = Element('textarea')
const Textarea2 = Element('textarea')
const Checkbox = Element('input', 'checkbox')

const ProfileDataForm = (props) => {
    return <form onSubmit = {props.handleSubmit}>
        <button>save</button>
        {error && <div className={style.formSummaryError}>
            {error}
        </div>
        }
        <span>{props.profile.fullName}</span>
        <div>
            <b>About me: </b>
            <Field placeholder='about me..' name='aboutMe' component={Textarea1}/>
        </div>
        <div>
            <b>Looking for a job: </b>
            <Field placeholder='Looking for a job..' name='lookingForAJob' component={Checkbox}/>
        </div>
        <div>
            <b>My work skills: </b>
            <Field placeholder='about me..' name='lookingForAJobDescription' component={Textarea2}/>
        </div>
        <div>
            <b>Contacts</b>: { Object.keys(props.profile.contacts).map(key => {
                return <Field key={key} placeholder={key} name={'contact' + key} component = {Input}/>})}
        </div>
    </form>
}

export const ProfileDataFormRedux = reduxForm({form: 'editProfileData'})(ProfileDataForm)
