import React from 'react'
import style from './Users.module.css'
import photoUser from '../../img/user.svg'
import * as axios from 'axios'

class Users extends React.Component{

    componentDidMount() {

        axios.get("https://social-network.samuraijs.com/api/1.0/users").then((response) => {
            this.props.setUsers(response.data.items)
            this.props.setCountPage(response.data.totalCount)
            debugger
        })
        // props.setUsers([
        //     {"id": 1, followed: true, "fullName": "Dmitry", "status": 'I am a boss', photoUrl: "https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg", location: {city: "Minsk", country: "Belarus"}},
        //     {"id": 2, followed: false, "fullName": "Masha ", "status": 'ha ah ha', photoUrl: "https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg", location: {city: "Kokshetau", country: "Kazakhstan"}},
        //     {"id": 3, followed: false, "fullName": "Masat", "status": 'Hello. how you doing', photoUrl: "https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg", location: {city: "Astana", country: "Kazakhstan"}},
        //     {"id": 4, followed: true, "fullName": "Gulmira", "status": 'Hello world!', photoUrl: "https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg", location: {city: "Astana", country: "Kazakhstan"}}
        // ])
    }

    pageEvent = (p) => {
        this.props.setCurrentPage(p);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`).then((response) => {
            this.props.setUsers(response.data.items)
        })
    }

    render() {

        let pages = [];

        let pagesCount = Math.ceil(this.props.countPage / this.props.pageSize);



        for (let i = 1; i < pagesCount; i++){
            pages.push(i)
        }

        return <div>
            <div className={style.pagination}>
                { pages.map(p => <span className={this.props.currentPage === p && style.currentPage} onClick={ ()  => this.pageEvent(p) }>p</span>) }
            </div>

            {this.props.state.map(user =>
                <div key={user.id} className={style.user}>
                <span>
                    <span><img className={style.avaImg} src={user.photos.small != null ? user.photos.small : photoUser}/></span>
                    <span><button onClick={()=>{this.props.switch_follow(user.id)}}>{user.followed ? "Unfollow" : "Follow"}</button></span>
                </span>
                    <span>
                    <span>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                    </span>
                    <span>
                        <div>{"user.location.city"}</div>
                        <div>{"user.location.country"}</div>
                    </span>
                </span>
                </div>
            )
            }
        </div>
    }

}

export default Users
