const ADD_NEW_MESSAGE = "ADD-NEW-MESSAGE";
const UPDATE_NEW_MESSAGE = "UPDATE-NEW-MESSAGE";
let id = 4;

let dialogs_reducer = (state, action) => {

    switch (action.type) {

        case ADD_NEW_MESSAGE:
            let newMessage =  {"id": id, "message": state.newMessageText};
            id+=2;

            if(state.newMessageText.length > 0){
                state.messageData.push(newMessage);
                state.newMessageText = '';
            }
            return state

        case UPDATE_NEW_MESSAGE:
            state.newMessageText = action.newMessage;
            return state
    }

    return state
}
export const addMessageActionCreater = () => ({type: ADD_NEW_MESSAGE});
export const updateMessageActionCreater = (message) => ({type: UPDATE_NEW_MESSAGE, newMessage: message});

export default dialogs_reducer
