import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import {addPostActionCreater, updateNewPostActionCreater} from "../../../redux/store";

const MyPosts = (props) => {

    let newWord = React.createRef();

    let addPost = () =>{
        props.dispatch(addPostActionCreater());
    }

    let updateNewPost = () => {
        let text = newWord.current.value;
        if(text.length){
            props.dispatch(updateNewPostActionCreater(text));
        }
    }

  return (
    <div className={s.myPostWrap}>
      <h3>My posts</h3>
      <div className={s.sendText}>
        <textarea placeholder='Enter message...' onChange={ updateNewPost } value={ props.store.getState().profilePage.newPostText } ref={ newWord } />
        <button className={s.btn} onClick={ addPost }>Add post</button>

      </div>
      <div className={s.posts}>
          {props.store.getState().profilePage.postsData.map((post) =>
              <Post message = {post.message} likeCount = {post.likesCount} />
          )}
      </div>
    </div>
  )

}

export default MyPosts;
