import React from 'react';
import Profile from "./Profile";
import {connect} from "react-redux";
import {check_auth} from "../../redux/profile_reducer";
import {withRouter} from "react-router-dom";
import {withAuthRedirect} from "../HOC/withAuthRedirect";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId;
        if(!userId) userId = 2
        this.props.check_auth(userId)
    }

    render() {
        return (
            <Profile state = {this.props.state}/>
        )
    }
}


let AuthRedirectComponent = () => {

    return <ProfileContainer />
}

let mapStateToProps = (state) => ({
    state: state.profilePage,
    isAuth: state.auth.isAuth
});

let WithUrlDataContainerComponent = withRouter(AuthRedirectComponent);

export default connect(mapStateToProps, {check_auth})(WithUrlDataContainerComponent);
