let rerenderEntireTree = () => {
    console.log('State changed');
}

let state = {
    dialogPage: {
        userData: [
            {"id": 1, "name": "Dmitri"},
            {"id": 2, "name": "Svetlana"},
            {"id": 3, "name": "Nauryz"},
            {"id": 4, "name": "Dinmukhamed"},
            {"id": 5, "name": "Mukhit"}
        ],
        messageData: [
            {"id": 1, "message": "Yo"},
            {"id": 2, "message": "Hello"},
            {"id": 1, "message": "How are you"}
        ]
    },
    profilePage: {
        postsData: [
            {"id": 1, "message": "Hello, how are you?", "likesCount": 5},
            {"id": 2, "message": "My, first post!", "likesCount": 2},
            {"id": 3, "message": "Hello, everyone", "likesCount": 10},
        ],
        newPostText: 'DikushYo'
    },
    navbar: {
        navData:[
            {"id": 1, "href": "News", "name": "News", "photo": "https://image.flaticon.com/icons/svg/3158/3158703.svg"},
            {"id": 2, "href": "profile", "name": "Profile", "photo": "https://image.flaticon.com/icons/svg/1077/1077114.svg"},
            {"id": 3, "href": "dialogs", "name": "Message", "photo": "https://image.flaticon.com/icons/svg/2462/2462719.svg"},
            {"id": 4, "href": "music", "name": "Music", "photo": "https://image.flaticon.com/icons/svg/876/876817.svg"},
            {"id": 5, "href": "settings", "name": "Settings", "photo": "https://image.flaticon.com/icons/svg/889/889744.svg"}
        ]
    },
    newsPage: {
        news: [
            {id: 1, title: "Hello everyone. it is my first news post!", photo: "https://sun1.dataix-kz-akkol.userapi.com/LfIHTeWVcS-8YZ_aHd92yzk14ja88dRD_nvHPQ/w073PwJeeiw.jpg", comments: [{userId: 1, commentId: 1, text: "its awesome!"}]},
            {id: 2, title: "Today wether is good", photo: "https://sun1.dataix-kz-akkol.userapi.com/LfIHTeWVcS-8YZ_aHd92yzk14ja88dRD_nvHPQ/w073PwJeeiw.jpg", comments: [{userId: 2, commentId: 2, text: "Kazakhstan!!!"}]},
            {id: 3, title: "Im go to school", photo: "https://sun1.dataix-kz-akkol.userapi.com/LfIHTeWVcS-8YZ_aHd92yzk14ja88dRD_nvHPQ/w073PwJeeiw.jpg", comments: [{userId: 3, commentId: 3, text: "COol!"}]},
            {id: 4, title: "Why he not me?!", photo: "https://sun1.dataix-kz-akkol.userapi.com/LfIHTeWVcS-8YZ_aHd92yzk14ja88dRD_nvHPQ/w073PwJeeiw.jpg", comments: [{userId: 4, commentId: 4, text: "Go walk with me>"}]}
        ]
    }
}

export const addPost = () =>{

    let newPost = {
        "id": 5,
        "message": state.profilePage.newPostText,
        "likesCount": 14
    }

    if(state.profilePage.newPostText.length > 0){
        state.profilePage.postsData.push(newPost);
        state.profilePage.newPostText = '';
        rerenderEntireTree(state);
    }
}

export const updateNewPostText = (newText) => {
    console.log(newText)
    if(newText.length > 0){
        state.profilePage.newPostText = newText;
        rerenderEntireTree(state);
    }
}

export const subscribe = (observer) => {
    rerenderEntireTree = observer;  // observer
}

export default state
