import React from 'react';
import s from './NewsItem.module.css'

const NewsItem = ({post}) => {
    return (
        <div className={s.newsItem}>
            <span className={s.user}> User - {post.id} </span>
            <span className={s.newsTitle}> {post.title} </span>
            <div><img src={post.photo} alt="Photo"/></div>
            <div className={s.reviewCount}> <span> likes - {post.likesCount} </span>  <span> Comments - {post.CommentsCount} </span> </div>
            <div className={s.commentsArea}>
                <input placeholder='Write your comment'/>
                {post.comments.map(item => (
                    <div className={s.commentsItem}>
                        <span>User - {item.userId} </span>
                        <span>{item.text}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default NewsItem;
