import React, { useState } from 'react';
import  s from './ProfileInfo.module.css'
import Preloader from '../../common/Preloader/Preloader';
import photoUser from '../../../img/user.svg'
import ProfileStatusWithHooks from "./ProfileStatusWithHooks";
import {ProfileDataFormRedux} from "./ProfileDataForm"

const ProfileInfo = (props) => {

    const [editMode, setEditMode] = useState(false)

    if (!props.profile && !props.photo){
        return <Preloader/>
    }

    const onInputPhoto = (e) => {
        if(e.target.files.length){
            props.updatePhoto(e.target.files[0])
        }
    }
    
    const onSubmit = (data) => {
        props.saveProfile(data).then(() => {
            setEditMode(false)
        });
    }

    return (<div>
        <div className={s.descriptionBlock}>
            <div style={{display:'flex'}}>
                <img src={ props.photo || photoUser } alt="photo" className={s.avaImg}/>
                {props.isOwner && <input type="file" onChange={onInputPhoto} className={s.inputPhoto}/>}
            </div>
            <div><ProfileStatusWithHooks status = {props.status} updateStatus = {props.updateStatus}/></div>
            { editMode 
                ? <ProfileDataFormRedux initialValues = {props.profile} profile = {props.profile} onSubmit = {onSubmit}/>
                : <ProfileData profile = {props.profile} onEditMode = {() => setEditMode(true)} isOwner={props.isOwner}/>
            }
            
        </div>
    </div>);
}

const Contact = ({contactTitle, contactValue}) => {
    return <div className={s.contact}><b>{contactTitle}</b>: {contactValue}</div>
}

const ProfileData = ({profile, ...props}) => {

    return <div style={{position: 'relative'}} className={s.profileData}>
        {props.isOwner && <button onClick = {props.onEditMode} className = {s.editBtn + ' ' + s.btn}/>}
        <p>{profile.fullName}</p>
        <div><b>About me: </b><span>{profile.aboutMe}</span></div>
        <div><b>Looking for a job: </b><span>{profile.lookingForAJob ? 'yes' : 'no'}</span></div>
        {profile.lookingForAJob && <div><b>My work skills: </b><span>{profile.lookingForAJob && profile.lookingForAJobDescription}</span></div>}
        <div>
            <b>Contacts</b>: {profile.contacts && Object.keys(profile.contacts).map(key => {
                return profile.contacts[key] && <Contact key={key} contactTitle={key} contactValue={profile.contacts[key]}/>})}
        </div>
    </div>
}

export default ProfileInfo;
