import {AuthAPI} from "../api/api";

const SET_NEW_AUTH = 'SET_NEW_AUTH'

let initialState = {
    id: null,
    login: null,
    email: null
}

let header_reducer = (state = initialState, action) => {

    switch (action.type) {

        case SET_NEW_AUTH: {
            return {
                ...state,
                ...action.data,
                isAuth: true
            }
        }
        default:
            return state
    }
}

export const setNewAuth = (id, login, email) => ({type: SET_NEW_AUTH, data: {id, login, email, isAuth: true}})

export const checkAuth = () => {
    return (dispatch) => {
        AuthAPI.checkAuth()
            .then((data) => {
                if (data.resultCode === 0) {
                    let {id, login, email} = data.data
                    dispatch(setNewAuth(id, login, email))
                }
            })
    }
}

export default header_reducer
