import React, { useState } from 'react';
import  s from './ProfileInfo.module.css'
import Preloader from '../../common/Preloader/Preloader';
import photoUser from '../../../img/user.svg'
import ProfileStatusWithHooks from "./ProfileStatusWithHooks";
import { reduxForm, Field } from 'redux-form';
import { Element } from '../../common/FormControl/FormControl';

const ProfileInfo = (props) => {

    const [editMode, setEditMode] = useState(false)

    if (!props.profile){
        return <Preloader/>
    }

    const onInputPhoto = (e) => {
        if(e.target.files.length){
            props.updatePhoto(e.target.files[0])
        }
    }
    
    onSubmit = (data) => {

    }

    return (<div>
        <div className={s.descriptionBlock}>
            <div>
                <img src={ props.photo || photoUser } alt="photo" className={s.avaImg}/>
                {props.isOwner && <input type="file" onChange={onInputPhoto}/>}
            </div>
            <div><ProfileStatusWithHooks status = {props.status} updateStatus = {props.updateStatus}/></div>
            <ProfileData profile = {props.profile}/>
        </div>
    </div>);
}

const Contact = ({contactTitle, contactValue}) => {
    return <div className={s.contact}><b>{contactTitle}</b>: {contactValue}</div>
}

const ProfileData = ({profile}) => {

    return <div>
        <button>edit</button>
        <span>{profile.fullName}</span>
        <div><b>About me: </b><span>{profile.aboutMe}</span></div>
        <div><b>Looking for a job: </b><span>{profile.lookingForAJob ? 'yes' : 'no'}</span></div>
        {profile.lookingForAJob && <div><b>My work skills: </b><span>{profile.lookingForAJob ? 'yes' : 'no'}</span></div>}
        <div>
            <b>Contacts</b>: {profile.contacts && Object.keys(profile.contacts).map(key => {
                return <Contact key={key} contactTitle={key} contactValue={profile.contacts[key]}/>})}
        </div>
    </div>
}

const Input = Element('input')
const Textarea = Element('textarea')
const Checkbox = Element('checkbox')

const ProfileDataForm = ({profile}) => {
    return <form onSubmit = {props.handleSubmit}>
        <button>save</button>
        <span>{profile.fullName}</span>
        <div>
            <b>About me: </b>
            <Field placeholder='about me..' name='aboutMe' component={Textarea}/>
        </div>
        <div>
            <b>Looking for a job: </b>
            <Field placeholder='Looking for a job..' name='lookingForAJob' component={Checkbox}/>
        </div>
        {profile.lookingForAJob && <div><b>My work skills: </b><span>{profile.lookingForAJob ? 'yes' : 'no'}</span></div>}
        <div>
            <b>Contacts</b>: {profile.contacts && Object.keys(profile.contacts).map(key => {
                return <Field key={key} placeholder={key} name={'contact' + key} component = {Input}/>})}
        </div>
    </form>
}

const ProfileDataFormRedux = reduxForm({form: 'editProfileData'})(ProfileDataForm)

export default ProfileInfo;
