import React from 'react';
import {Formik, Field, Form} from 'formik'
import {FilterType} from '../../redux/users_reducer'

type PropsType = {
  onFilterChanged: (filter: FilterType) => void
}

const UsersSearchForm: React.FC<PropsType> = (props) => {

  const submit = (values: FilterType, {setSubmitting} : {setSubmitting: (isSubmitting: boolean) => void}) => {

    //let friendsValue = values.friends === "null" ? null : values.friends ? true : false
    props.onFilterChanged(values)
    setSubmitting(false)
  }
  
    return <div className="pageArea">
      <Formik
        initialValues={{ term: '', friends: null}}
        validate={values => {
          const errors = {};
          return errors;
        }}
        onSubmit = {submit}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field type="text" name="term" placeholder='Enter name...'/>
            <Field name="friends" as="select" placeholder="Favorite Color">
              <option value="true">Friends</option>
              <option value="false">Unfollowed users</option>
              <option value="null">All users</option>
            </Field>
            <button type="submit" className="btn" disabled={isSubmitting}>
              Find
            </button>
          </Form>
        )}
      </Formik>
    </div>
}
  
  export default UsersSearchForm;
