const ADD_NEW_MESSAGE = "ADD-NEW-MESSAGE";
let id = 4;

let initialState = {
    userData: [
        {"id": 1, "name": "Dmitri"},
        {"id": 2, "name": "Svetlana"},
        {"id": 3, "name": "Nauryz"},
        {"id": 4, "name": "Dinmukhamed"},
        {"id": 5, "name": "Mukhit"}
    ],
    messageData: [
        {
            id: 1,
            messages: [
                {"id": 1, "message": "Yo"},
                {"id": 2, "message": "Hello"},
                {"id": 1, "message": "How are you"}
            ]
        },
        {
            id: 2,
            messages: [
                {"id": 1, "message": "hello. how are you?"},
                {"id": 2, "message": "Hello"},
                {"id": 1, "message": "im good"}
            ]
        },
        {
            id: 3,
            messages: [
                {"id": 1, "message": "hi"},
                {"id": 2, "message": "Hello"},
                {"id": 1, "message": "what we get in home work"}
            ]
        },
        {
            id: 4,
            messages: [
                {"id": 1, "message": "drop our phootos"},
                {"id": 2, "message": "which?"},
                {"id": 1, "message": "from zerendy"}
            ]
        },
        {
            id: 5,
            messages: [
                {"id": 1, "message": "Hello classmates"},
                {"id": 2, "message": "Hello"},
                {"id": 1, "message": "wourld you like have hang out with me?"},
                {"id": 2, "message": "yes, go!"}
            ]
        }
    ]
}

let dialogs_reducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_NEW_MESSAGE: {

            if (action.newMessageText.length > 0) {
                return {
                    ...state,
                    messageData: state.messageData.map(item => {
                        if(action.id === item.id){
                            return [...item, {"id": 2, "message": action.newMessageText}]
                        }
                    })
                    //[...state.messageData, {"id": id, "message": action.newMessageText}],
                }
            }
            id += 2;
            return state
        }

        default: {
            return state
        }
    }
}
export const addMessage= (id, newMessageText) => ({type: ADD_NEW_MESSAGE, id, newMessageText});

export default dialogs_reducer
