import {ProfileAPI} from "../api/api";
import { stopSubmit } from "redux-form";

const ADD_NEW_POST = "ADD-NEW-POST";
const PROFILE_INFO = "PROFILE_INFO";
const My_PROFILE_INFO = "My_PROFILE_INFO";
const SET_STATUS = "SET_STATUS";
const SET_PHOTO = "SET_PHOTO";
const ADD_NEW_COMMENT = "ADD_NEW_COMMENT";
const UPDATE_LIKE_COUNT = "UPDATE_LIKE_COUNT";
const UPDATE_COMMENT_COUNT = "UPDATE_COMMENT_COUNT";
let id = 3;

let initialState = {
    postsData: [
        {
            id: 1,
            title: "Hello everyone. it is my first news post!",
            comments: [
                {
                    userId: 1, commentId: 1, fullName: 'Башаров Максим', text: "its awesome!", photo: 'https://i.pinimg.com/736x/8f/f6/08/8ff60864af4b3eebc06a8bbabcf0c21b.jpg'
                }, 
                {
                    userId: 2, commentId: 2, fullName: 'Башаров Максим', text: "How are you?", photo: 'https://avatars.mds.yandex.net/get-zen_doc/229614/pub_5db308ea5ba2b500b6bb0d1d_5db309208d5b5f00afb1fe92/scale_1200'
                }],
            likesCount: 5,
            CommentsCount: 2,
            liked: false
        },
        {
            id: 1,
            title: "Hello everyone. it is my first news post!",
            comments: [
                {
                    userId: 1, commentId: 1, fullName: 'Башаров Максим', text: "its awesome!", photo: 'https://i.pinimg.com/736x/8f/f6/08/8ff60864af4b3eebc06a8bbabcf0c21b.jpg'
                }, 
                {
                    userId: 2, commentId: 2, fullName: 'Башаров Максим', text: "How are you?", photo: 'https://avatars.mds.yandex.net/get-zen_doc/229614/pub_5db308ea5ba2b500b6bb0d1d_5db309208d5b5f00afb1fe92/scale_1200'
                }],
            likesCount: 5,
            CommentsCount: 2,
            liked: false
        },
        {
            id: 1,
            title: "Hello everyone. it is my first news post!",
            comments: [
                {
                    userId: 1, commentId: 1, fullName: 'Башаров Максим', text: "its awesome!", photo: 'https://i.pinimg.com/736x/8f/f6/08/8ff60864af4b3eebc06a8bbabcf0c21b.jpg'
                }, 
                {
                    userId: 2, commentId: 2, fullName: 'Башаров Максим', text: "How are you?", photo: 'https://avatars.mds.yandex.net/get-zen_doc/229614/pub_5db308ea5ba2b500b6bb0d1d_5db309208d5b5f00afb1fe92/scale_1200'
                }],
            likesCount: 5,
            CommentsCount: 2,
            liked: false
        },
        {
            id: 1,
            title: "Hello everyone. it is my first news post!",
            comments: [
                {
                    userId: 1, commentId: 1, fullName: 'Башаров Максим', text: "its awesome!", photo: 'https://i.pinimg.com/736x/8f/f6/08/8ff60864af4b3eebc06a8bbabcf0c21b.jpg'
                }, 
                {
                    userId: 2, commentId: 2, fullName: 'Башаров Максим', text: "How are you?", photo: 'https://avatars.mds.yandex.net/get-zen_doc/229614/pub_5db308ea5ba2b500b6bb0d1d_5db309208d5b5f00afb1fe92/scale_1200'
                }],
            likesCount: 5,
            CommentsCount: 2,
            liked: false
        }
    ],
    profile: null,
    status: '',
    photos: {
        small: '',
        large: ''
    }
}

let profile_reducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_NEW_POST: {

            if (action.newPostText.length > 0) {
                return {
                    ...state,
                    postsData: [...state.postsData, {
                        "id": 5,
                        "message": action.newPostText,
                        "likesCount": 14
                    }],
                    newPostText: ''
                }
            }

            return state

        }

        case PROFILE_INFO: {

            return {
                ...state,
                profile: action.profile
            }
        }

        case My_PROFILE_INFO: {

            return {
                ...state,
                profile: action.profile,
                photos: action.profile.photos
            }
        }

        case SET_STATUS: {

            return {
                ...state,
                status: action.status
            }
        }
        case SET_PHOTO: {

            return {
                ...state,
                profile: {...state.profile, photos: action.photoUrl },
                photos: action.photoUrl
            }
        }

        default: {
            return state
        }
    }
}

export const addPost = (newPostText) => ({type: ADD_NEW_POST, newPostText});
export const profileInfo = (profile) => ({type: PROFILE_INFO, profile});
export const myProfileInfo = (profile) => ({type: My_PROFILE_INFO, profile});
export const setStatus = (status) => ({type: SET_STATUS, status});
export const setPhoto = (photoUrl) => ({type: SET_PHOTO, photoUrl});


export const check_auth = (userId) => async (dispatch, getState) => {
    const data = await ProfileAPI.checkAuth(userId)
    dispatch(profileInfo(data))
}

export const getStatus = (userId) => (dispatch) => {
    ProfileAPI.getStatus(userId).then((data) => {
        dispatch(setStatus(data))
    })
}

export const updateStatus = (status) => (dispatch) => {
    ProfileAPI.updateStatus(status).then((data) => {
        if (data.resultCode === 0) {
            dispatch(setStatus(status));
        }
    })
}

export const updatePhoto = (photo) => (dispatch) => {
    ProfileAPI.updateMainPhoto(photo).then((data) => {
        if (data.resultCode === 0) {
            dispatch(setPhoto(data.data.photos));
        }
    })
}

export const saveProfile = (profile) => async (dispatch, getState) => {
    const userId = getState().auth.id;
    const response = await ProfileAPI.saveProfile(profile);

        if (response.data.resultCode === 0) {
            dispatch(check_auth(userId));
        } else {
            dispatch(stopSubmit("editProfileData", {_error: response.data.messages[0]}));
            return Promise.reject(response.data.messages[0]);
        }
}

export const check_auth_me = () => async (dispatch, getState) => {
    const myId = getState().auth.id;
    const data = await ProfileAPI.checkAuth(myId)
    dispatch(myProfileInfo(data))
}


export default profile_reducer
