import React from 'react'
import style from './Users.module.css'
import photoUser from '../../img/user.svg'
import {NavLink} from "react-router-dom";

const Users = (props) => {

    let pages = [];

    let pagesCount = Math.ceil(props.pageCount / props.pageSize);

    for (let i = 1; i < pagesCount; i++) {
        pages.push(i)
    }

    const switch_follow = (followed, userId) => {
        if (followed) {
            props.unfollow(userId)
        } else {
            props.follow(userId)
        }
    }

    return <div>

        <div className={style.pagination}>
            {pages.map(p => <span className={props.currentPage === p ? style.currentPage : style.pag_item}
                                  onClick={() => props.pageEvent(p)} key={p}>{p}</span>)}
        </div>

        {props.state.map(user =>
            <div key={user.id} className={style.user}>
                <NavLink to={"/profile/" + user.id}>
                        <span>
                            <img className={style.avaImg}
                                 src={user.photos.small !== null ? user.photos.small : photoUser}/>
                        </span>
                </NavLink>
                <span>
                    <span>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                <button onClick={() => switch_follow(user.followed, user.id)}
                        disabled={props.toggleProgressArr.some(id => id === user.id)}>{user.followed ? "Unfollow" : "Follow"}
                </button>
                    </span>
                </span>

                <span>
                    <div>{"user.location.city"}</div>
                    <div>{"user.location.country"}</div>
                </span>
            </div>
        )}
    </div>
}

export default Users
