import {AuthAPI, LoginAPI} from "../api/api";

const SET_NEW_AUTH = 'SET_NEW_AUTH'

let initialState = {
    id: null,
    login: null,
    email: null,
    isAuth: false
}

let header_reducer = (state = initialState, action) => {

    switch (action.type) {

        case SET_NEW_AUTH: {
            return {
                ...state,
                ...action.data,
                isAuth: true
            }
        }
        default:
            return state
    }
}

export const setNewAuth = (id, login, email) => ({type: SET_NEW_AUTH, data: {id, login, email}})

export const checkAuthMe = () => (dispatch) => {
    AuthAPI.checkAuthMe()
        .then((data) => {
            if (data.resultCode === 0) {
                let {id, login, email} = data.data
                dispatch(setNewAuth(id, login, email))
            }
        })

}

export const checkLoginMe = (dataObj) => (dispatch) => {
    LoginAPI.checkLogin(dataObj)
        .then((response) => {
            if (response.data.resultCode === 0) {
                debugger
                checkAuthMe();
            }
        })
}

export default header_reducer
