import React from 'react';
import Profile from "./Profile";
import {connect} from "react-redux";
import {check_auth, profileInfo} from "../../redux/profile_reducer";
import {withRouter} from "react-router-dom";
import {ProfileAPI} from "../../api/api";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId;
        if(!userId) userId = 2
        this.props.check_auth(userId)
        // ProfileAPI.checkAuth(userId).then((data) => {
        //     this.props.profileInfo(data)
        // })
    }

    render() {
        return (
            <Profile state = {this.props.state}/>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        state: state.profilePage
    }
}

let UrlUserContainer = withRouter(ProfileContainer)

export default connect( mapStateToProps, { check_auth })(UrlUserContainer);
