import React from 'react';
import {addPostActionCreater, updateNewPostActionCreater} from "../../../redux/profile_reducer";
import MyPosts from "./MyPosts";
import StoreContext from "../../../StoreContext";

const MyPostsContainer = (props) => {

    return <StoreContext>
        {
            store => {
                let state = store.getState().profilePage;

                let addPost = () =>{
                    store.dispatch(addPostActionCreater());
                }

                let updateNewPost = (text) => {
                    store.dispatch(updateNewPostActionCreater(text));
                }


                return ( <MyPosts updateNewPost = { updateNewPost } addPost = { addPost } state = {state}/> )
            }
        }
    </StoreContext>

}

export default MyPostsContainer;
