import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import {addPostActionCreater, updateNewPostActionCreater} from "../../../redux/profile_reducer";

const MyPosts = (props) => {

    let newWord = React.createRef();

    let onAddPost = () =>{
        //props.dispatch(addPostActionCreater());
        props.addPost();
    }

    let onUpdateNewPost = () => {
        let text = newWord.current.value;
        props.updateNewPost(text)
        //props.dispatch();
    }

  return (
    <div className={s.myPostWrap}>
      <h3>My posts</h3>
      <div className={s.sendText}>
        <textarea placeholder='Enter message...' onChange={ onUpdateNewPost } value={ props.state.newPostText } ref={ newWord } />
        <button className={s.btn} onClick={ onAddPost }>Add post</button>

      </div>
      <div className={s.posts}>
          {props.state.postsData.map((post) =>
              <Post message = {post.message} likeCount = {post.likesCount} />
          )}
      </div>
    </div>
  )

}

export default MyPosts;
