import React from 'react'
import style from './Paginator.module.css'

const Paginator = ({currentPage, pageCount, pageSize, pageEvent}) => {

    let pages = [];

    let pagesCount = Math.ceil(props.pageCount / props.pageSize);

    for (let i = 1; i < pagesCount; i++) {
        pages.push(i)
    }

    return <div className={style.pagination}>
    {pages.map(p => <span className={props.currentPage === p ? style.currentPage : style.pag_item}
                          onClick={() => props.pageEvent(p)} key={p}>{p}</span>)}
    </div>
}

export default Paginator
