import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import {Field, reduxForm} from "redux-form";

const MyPosts = (props) => {

    let onAddPost = (message) => {
        props.addPost(message.newMyPostText);
    }


    return (
        <div className={s.myPostWrap}>
            <h3>My posts</h3>
            <MyPostFormRedux onSubmit = {onAddPost}/>
            <div className={s.posts}>
                {props.state.postsData.map((post) =>
                    <Post message={post.message} likeCount={post.likesCount}/>
                )}
            </div>
        </div>
    )

}

const myPostForm = (props) => {

    return (
        <form className={s.sendText} onSubmit={props.handleSubmit}>
            <Field placeholder='Enter message...' name='newMyPostText' component='textarea'/>
            <button className={s.btn}>Add post</button>
        </form>
    )
}

const MyPostFormRedux = reduxForm({form: 'myPostForm'})(myPostForm)

export default MyPosts;
