import React from 'react'
import style from './Users.module.css'
import photoUser from '../../img/user.svg'
import {NavLink} from "react-router-dom";
import Paginator from '../common/paginator/Paginator';

const Users = (props) => {

    const switch_follow = (followed, userId) => {
        if (followed) {
            props.unfollow(userId)
        } else {
            props.follow(userId)
        }
    }

    return <div>

        <Paginator 
            currentPage = {props.currentPage}
            pageCount = {props.pageCount}
            pageSize = {props.pageSize}
            pageEvent = {props.pageEvent}/>

        {props.state.map(user =>
            <div key={user.id} className={style.user}>
                <NavLink to={"/profile/" + user.id}>
                        <span>
                            <img className={style.avaImg}
                                 src={user.photos.small !== null ? user.photos.small : photoUser}/>
                        </span>
                </NavLink>
                <span className={style.userInfo}>
                    <div>{user.name}</div>
                    <div>{user.status}</div>
                    <button className={style.btn + ' ' + (user.followed ? style.btnBlue : style.btnRed)} onClick={() => switch_follow(user.followed, user.id)}
                            disabled={props.toggleProgressArr.some(id => id === user.id)}>{user.followed ? "Unfollow" : "Follow"}
                    </button>
                </span>

                {/*<span>*/}
                {/*    <div>{"user.location.city"}</div>*/}
                {/*    <div>{"user.location.country"}</div>*/}
                {/*</span>*/}
            </div>
        )}
    </div>
}

export default Users
