import {AuthAPI, LoginAPI, SecurityAPI} from "../api/api";
import {stopSubmit} from "redux-form";

const SET_NEW_AUTH = 'SET_NEW_AUTH'
const SET_NEW_CAPTCHA = 'SET_NEW_CAPTCHA'

let initialState = {
    id: null,
    login: null,
    email: null,
    isAuth: false,
    captcha: null
}

let header_reducer = (state = initialState, action) => {

    switch (action.type) {

        case SET_NEW_AUTH:
        case SET_NEW_CAPTCHA: {
            return {
                ...state,
                ...action.data
            }
        }
        default:
            return state
    }
}

export const setNewAuth = (id, login, email, isAuth) => ({type: SET_NEW_AUTH, data: {id, login, email, isAuth}})
export const setNewCaptcha = (captcha) => ({type: SET_NEW_CAPTCHA, data: {captcha}})

export const checkAuthMe = () => (dispatch) => {
    return AuthAPI.checkAuthMe()
        .then((data) => {
            if (data.resultCode === 0) {
                let {id, login, email} = data.data
                dispatch(setNewAuth(id, login, email, true))
            }
        })
}

export const checkLoginMe = (email, password, rememberMe, captcha) => (dispatch) => {
    LoginAPI.checkLogin(email, password, rememberMe, captcha)
        .then((response) => {
            if (response.data.resultCode === 0) {
                dispatch(checkAuthMe());
            } else {
                if(response.data.resultCode === 10){
                    debugger
                    dispatch(getCaptcha())
                }
                let message = response.data.messages.length > 0 ? response.data.messages[0] : 'Some error!'
                dispatch(stopSubmit('login', {_error: message}))
            }
        })
}

export const logoutMe = (dataObj) => (dispatch) => {
    LoginAPI.logout()
        .then((response) => {
            if (response.data.resultCode === 0) {
                dispatch(setNewAuth(null, null, null, false))
            }
        })
}

export const getCaptcha = () => (dispatch) => {
    SecurityAPI.getCaptcha()
        .then((response) => {
                dispatch(setNewCaptcha(response.url))
        })
}

export default header_reducer
