const ADD_NEW_MESSAGE = "ADD-NEW-MESSAGE";
const UPDATE_NEW_MESSAGE = "UPDATE-NEW-MESSAGE";
let id = 4;

let initialState = {
    userData: [
        {"id": 1, "name": "Dmitri"},
        {"id": 2, "name": "Svetlana"},
        {"id": 3, "name": "Nauryz"},
        {"id": 4, "name": "Dinmukhamed"},
        {"id": 5, "name": "Mukhit"}
    ],
    messageData: [
        {"id": 1, "message": "Yo"},
        {"id": 2, "message": "Hello"},
        {"id": 1, "message": "How are you"}
    ],
    newMessageText: ''
}

let dialogs_reducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_NEW_MESSAGE: {

            if (state.newMessageText.length > 0) {
                return {
                    ...state,
                    messageData: [...state.messageData, {"id": id, "message": state.newMessageText}],
                    newMessageText: ''
                }
            }
            id += 2;
            return state
        }
        case UPDATE_NEW_MESSAGE: {
            return {
                ...state,
                newMessageText: action.newMessage
            }
        }

        default: {
            return state
        }
    }
}
export const addMessage= () => ({type: ADD_NEW_MESSAGE});
export const updateMessage = (message) => ({type: UPDATE_NEW_MESSAGE, newMessage: message});

export default dialogs_reducer
