import { Field, Form, Formik, useFormik, useFormikContext } from 'formik'
import React from 'react'
import { ChangeEvent } from 'react';

const HeaderSearchForm = () => {

    const submit = (values: FormValuesType, {setSubmitting} : {setSubmitting: (isSubmitting: boolean) => void}) => {
        setSubmitting(false)
      }

    // const submitForm = useFormikContext()
    // const formik = useFormik({
    //     initialValues: {
    //         fullName: "",
    //     },
    //     onSubmit: values => {
    //         console.log(values.fullName)
    //       alert(JSON.stringify(values, null, 2));
    //     },
    //   });

    return <div>
        <Formik
        initialValues =  {{
            fullName: "",
        }}
        onSubmit = {submit}>
            <Form>
                <Field type = "text" name = "fullName" placeholder = "Enter name..."/>
            </Form>
        </Formik>
        {/* @ts-ignore*/}
        {/* <form onSubmit = {formik.handleSubmit} onChange = {(e) => { formik.handleSubmit; submitForm() } }>
            <input 
                id = "name"
                name = "fullName"
                type = "text" 
                onChange = {formik.handleChange}
                placeholder = "Enter name..."/>
        </form> */}
    </div>
}

export default HeaderSearchForm

type FormValuesType = {
    fullName: string
}
