import React from 'react';
import s from './Profile.module.css';
import Profile from "./Profile";
import {connect} from "react-redux";
import * as axios from "axios";
import {profileInfo} from "../../redux/profile_reducer";

class ProfileContainer extends React.Component {
    componentDidMount() {
        axios.get("https://social-network.samuraijs.com/api/1.0/profile/1060").then((response) => {
            this.props.profileInfo(response.data)
        })
    }

    render() {

        return (
            <Profile state = {this.props.profile}/>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        profile: state.profilePage
    }
}

export default connect( mapStateToProps, { profileInfo })(ProfileContainer);
