import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import StoreContext from "../../../StoreContext";

const MyPosts = (props) => {

    return <StoreContext>
        {
            (store) => {

                let onAddPost = () =>{
                    props.addPost();
                }

                let onUpdateNewPost = (e) => {
                    let text = e.target.value;
                    props.updateNewPost(text)
                }

                return (
                    <div className={s.myPostWrap}>
                        <h3>My posts</h3>
                        <div className={s.sendText}>
                            <textarea placeholder='Enter message...' onChange={ onUpdateNewPost } value={ props.state.newPostText }/>
                            <button className={s.btn} onClick={ onAddPost }>Add post</button>

                        </div>
                        <div className={s.posts}>
                            {props.state.postsData.map((post) =>
                                <Post message = {post.message} likeCount = {post.likesCount} />
                            )}
                        </div>
                    </div>
                )
            }
        }
    </StoreContext>

}

export default MyPosts;
