const SET_NEW_AUTH = 'SET_NEW_AUTH'

let initialState = {
    id: null,
    login: null,
    email: null,
    isAuth: false
}

let header_reducer = (state = initialState, action) => {

    switch (action.type) {

        case SET_NEW_AUTH: {

            return {
                ...state,
                ...action.data
            }
        }
    }

    return state
}

export const setNewAuth = (id, login, email) => ({type: SET_NEW_AUTH, data: {id, login, email}})

export default header_reducer
