import React from 'react'
import style from './Paginator.module.css'

const Paginator = ({currentPage, pageCount, pageSize, pageEvent}) => {

    let pages = [];


    let pagesCount = Math.ceil(pageCount / pageSize);

    for (let i = 1; i < pagesCount; i++) {
        pages.push(i)
    }

    let [portionNumber, setPortionNumber] = useState(1);
    let rightBorderPagination = (portionNumber - 1) * pageSize + 1;
    let leftBorderPagination = portionNumber * pageSize; 


    return <div className={style.pagination}>
    {pages.map(p => <span className={currentPage === p ? style.currentPage : style.pag_item}
                          onClick={() => pageEvent(p)} key={p}>{p}</span>)}
    </div>
}

export default Paginator
