import React from 'react';
import News from "./News";
import {connect} from "react-redux";
import {addNewComment, updateNewComment} from "../../redux/news_reducer";

const mapStateToProps = (state) => {

    return {
        state: state.newsPage
    }
}

export default connect(mapStateToProps, { addNewComment, updateNewComment })(News)
