import React, { useEffect } from 'react';
import Profile from "./Profile";
import { useDispatch, useSelector} from "react-redux";
import {check_auth, getStatus} from "../../redux/profile_reducer";
import {withRouter, RouteComponentProps} from "react-router-dom";
import {compose} from "redux";
import {withAuthRedirect} from "../HOC/withAuthRedirect"
import { AppStateType } from '../../redux/store-redux';

const ProfileContainer:React.FC<PropsType & RouteComponentProps<RouteParamsProps>> = (props) => {

    const dispatch = useDispatch()
    const authId = useSelector((state: AppStateType) => state.auth.id)

    const refreshProfile = () => {
        let userId: number = +props.match.params.userId;
        if (!userId) userId = authId as number
        dispatch(check_auth(userId))
        dispatch(getStatus(userId))
    }

    useEffect(() => {
        refreshProfile();
    }, [props.match.params.userId])

        return (
            <Profile isOwner={!props.match.params.userId} />
        )
}

export default compose<React.ComponentType>(
    withRouter,
    withAuthRedirect
)(ProfileContainer)



type PropsType = {
    isOwner: boolean
}

type RouteParamsProps = {
    userId: string 
}
