import React, { useState } from 'react';
import {StoriesType} from '../../../redux/news_reducer'

type PropsType = {
    story: StoriesType
}
    
const imgStyles = {width: "110px", height: "185px", borderRadius: "5px", cursor: "pointer"}

const StoriesItem: React.FC<PropsType & HTMLCollectionOf<HTMLBodyElement>> = ({story}) => {

    const [bigMode, setBigMode] = useState(false)

    const onStoryClick = () => {
        setBigMode(true)
        let body = document.getElementsByTagName('body')
        body.classList.add("")
    }

    return <div style={{marginLeft: "15px"}}>
        <img src={story.photo} alt="Photo" style={imgStyles} onClick={onStoryClick} />
        {
            bigMode && <div>
                    <div id="overlay" onClick={() => setBigMode(false)}></div>
                    <div id="magnify">
                        <img src={story.photo} />
                        <div id="close-popup" onClick={() => setBigMode(false)}><i></i></div>
                    </div>
                </div>
        }
    </div>
}

export default StoriesItem
