import React from 'react';
import {connect} from "react-redux";
import {
    follow,
    unfollow,
    getUsers,
    setCurrentPage,
    setPageCount,
    setUsers,
    switch_follow,
    toggleProgress
} from "../../redux/users_reducer";
import Users from "./Users";
import Preloader from "../common/Preloader/Preloader";
import {withAuthRedirect} from "../HOC/withAuthRedirect";
import {checkAuth} from "../../redux/header_reducer";
import {compose} from "redux";

class UsersContainer extends React.Component {

    componentDidMount() {
        this.props.getUsers(this.props.currentPage, this.props.pageSize)
    }

    pageEvent = (p) => {
        this.props.getUsers(p, this.props.pageSize)
    }

    render() {
        return <>
            {this.props.isFetching ? <Preloader/> :
                <Users
                    currentPage={this.props.currentPage}
                    pageCount={this.props.pageCount}
                    pageSize={this.props.pageSize}
                    switch_follow={this.props.switch_follow}
                    state={this.props.state}
                    pageEvent={this.pageEvent}
                    toggleProgressArr = {this.props.toggleProgressArr}
                    toggleProgress = {this.props.toggleProgress}
                    follow = {this.props.follow}
                    unfollow = {this.props.unfollow}
                />}
        </>
    }
}

const mapStateToProps = (state) => {

    return {
        state: state.usersPage.usersData,
        currentPage: state.usersPage.currentPage,
        pageCount: state.usersPage.pageCount,
        pageSize: state.usersPage.pageSize,
        isFetching: state.usersPage.isFetching,
        toggleProgressArr: state.usersPage.toggleProgressArr
    }
}

export default compose(
    withAuthRedirect,
    connect(mapStateToProps, {
        switch_follow,
        setCurrentPage,
        setPageCount,
        toggleProgress,
        getUsers,
        follow,
        unfollow
    })
)(UsersContainer)




