import React from 'react';
import {connect} from "react-redux";
import {setCurrentPageAC, setPageCountAC, setUsersAC, switch_followAC} from "../../redux/users_reducer";
import Users from "./Users";

const mapStateToProps = (state) => {

    return {
        state: state.usersPage.usersData,
        currentPage: state.usersPage.currentPage,
        countPage: state.usersPage.countPage,
        pageSize: state.usersPage.pageSize
    }
}

const mapDispatchToProps = (dispatch) => {

    return {
        switch_follow: (usersId) => {
            dispatch(switch_followAC(usersId))
        },
        setUsers: (users) => {
            dispatch(setUsersAC(users))
        },
        setCurrentPage: (currentPage) => {
            dispatch(setCurrentPageAC(currentPage))
        },
        setCountPage: (pageCount) => {
            dispatch(setPageCountAC(pageCount))
        }
    }
}

const UsersContainer = connect(mapStateToProps, mapDispatchToProps)(Users)

export default UsersContainer;
