import React from 'react'
import style from './Users.module.css'
import photoUser from '../../img/user.svg'
import * as axios from 'axios'
import {NavLink} from "react-router-dom";
import {UsersAPI} from "../../api/api";

const Users = (props) => {

    let pages = [];

    let pagesCount = Math.ceil(props.pageCount / props.pageSize);

    for (let i = 1; i < pagesCount; i++){
        pages.push(i)
    }

    const switch_follow = (followed, userId) => {
        if(followed){
            props.toggleProgress(userId)
            UsersAPI.unfollow(userId).then((data) => {
                props.switch_follow(userId)
            })
        } else {
            props.toggleProgress(userId)
            UsersAPI.follow(userId).then((data) => {
                props.switch_follow(userId)
            })
        }
    }

    return <div>

        <div className={style.pagination}>
            { pages.map(p => <span className={props.currentPage === p ? style.currentPage : style.pag_item} onClick={ ()  => props.pageEvent(p) } key={p}>{p}</span>) }
        </div>

        {props.state.map(user =>
            <div key={user.id} className={style.user}>
                <span>
                    <NavLink to  = {"/profile/" + user.id}>
                        <span><img className={style.avaImg} src={user.photos.small != null ? user.photos.small : photoUser} /></span>
                    </NavLink>
                    <span><button onClick={() => switch_follow(user.followed, user.id)} disabled={ props.toggleProgress.some(id => id == user.id)}>{ user.followed ? "Unfollow" : "Follow" }</button></span>
                </span>
                <span>
                    <span>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                    </span>
                    <span>
                        <div>{"user.location.city"}</div>
                        <div>{"user.location.country"}</div>
                    </span>
                </span>
            </div>
        )}
    </div>
}

export default Users
