import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {addMessageActionCreater, updateMessageActionCreater} from "../../redux/dialogs_reducer";
import Dialogs from "./Dialogs";

const DialogsContainer = (props) => {

    let state = props.store.getState().dialogPage;

    let addMessage = () => {
        props.store.dispatch(addMessageActionCreater())
    }

    let updateMessage = (text) => {
        props.store.dispatch(updateMessageActionCreater(text));
    }

    return (<Dialogs addMessage = {addMessage} updateMessage = {updateMessage} state = {state}/>)
}

export default DialogsContainer;
