import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {addMessageActionCreater, updateMessageActionCreater} from "../../redux/dialogs_reducer";

const Dialogs = (props) => {

    let addMessage = () => {
        props.dispatch(addMessageActionCreater())
    }

    let updateMessage = (e) => {
        let text = e.target.value;
        props.dispatch(updateMessageActionCreater(text));
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {props.store.getState().dialogPage.userData.map((user) =>
                    <DialogItem props = {user}/>
                )}

            </div>
            <div className={s.messages}>
                {props.store.getState().dialogPage.messageData.map((message) =>
                    <Message props = {message}/>
                )}
                <div className={s.inputArea}>
                    <textarea placeholder='Enter message...' className={s.input} onChange={ updateMessage } value={ props.state.newMessageText } />
                    <button className={s.btn} onClick={addMessage}>Send</button>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
