import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {addMessageActionCreater, updateMessageActionCreater} from "../../redux/store";

const Dialogs = ({state, store}) => {

    let addNewMessage = React.createRef();

    let addMessage = () => {
        store.dispatch(addMessageActionCreater())
    }

    let updateMessage = () => {
        let text = addNewMessage.current.value;
        store.dispatch(updateMessageActionCreater(text));
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {state.userData.map((user) =>
                    <DialogItem props = {user}/>
                )}

            </div>
            <div className={s.messages}>
                {state.messageData.map((message) =>
                    <Message props = {message}/>
                )}
                <div className={s.inputArea}>
                    <textarea placeholder='Enter message...' className={s.input} onChange={ updateMessage } value={ state.newMessage } ref={ addNewMessage } />
                    <button className={s.btn} onClick={addMessage}>Send</button>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
