import React from 'react';
import {connect} from "react-redux";
import {
    setCurrentPageAC,
    setPageCountAC,
    setUsersAC,
    switch_followAC,
    toggleIsFetchingAC
} from "../../redux/users_reducer";
import Users from "./Users";
import * as axios from "axios";

class UsersContainer extends React.Component {

    componentDidMount() {

        axios.get("https://social-network.samuraijs.com/api/1.0/users").then((response) => {
            this.props.setUsers(response.data.items)
            //this.props.setCountPage(response.data.totalCount)
        })
    }

    pageEvent = (p) => {
        this.props.setCurrentPage(p);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`).then((response) => {
            this.props.setUsers(response.data.items)
        })
    }

    render() {
        return <>
        <Users
            currentPage = {this.props.currentPage}
            pageCount = {this.props.pageCount}
            pageSize = {this.props.pageSize}
            switch_follow = {this.props.switch_follow}
            state = {this.props.state}
            pageEvent = {this.pageEvent}
        />
            </>
    }
}

const mapStateToProps = (state) => {

    return {
        state: state.usersPage.usersData,
        currentPage: state.usersPage.currentPage,
        pageCount: state.usersPage.pageCount,
        pageSize: state.usersPage.pageSize,
        isFetching: state.usersPage.isFetching
    }
}

const mapDispatchToProps = (dispatch) => {

    return {
        switch_follow: (usersId) => {
            dispatch(switch_followAC(usersId))
        },
        setUsers: (users) => {
            dispatch(setUsersAC(users))
        },
        setCurrentPage: (currentPage) => {
            dispatch(setCurrentPageAC(currentPage))
        },
        setCountPage: (pageCount) => {
            dispatch(setPageCountAC(pageCount))
        },
        toggleIsFetching: (isFetching) => {
            dispatch(toggleIsFetchingAC(isFetching))
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersContainer)
